angular.module("myApp.directive").directive("carouselPanel", function() {
    return {
        restrict: "A",
        templateUrl: "build/html/carousel-panel.html",
        controller: "CarouselPanelController",
        link: {
            post: function(scope, element, attribute) {},
            scope: {}
        }
    };
}).controller("CarouselPanelController", [ "$scope", "CarouselPanelConfig", "TextProvider", function($scope, CarouselPanel, ngInterpreter) {
    var config = CarouselPanel.config;
    var slides = $scope.slides = [];
    $scope.interpreter = ngInterpreter.getInterpreter();
    ngInterpreter.interpretProperty("text").in(config, "Carousel");
    $scope.interval = config.interval;
    $scope.addSlide = function(imageUrl, text) {
        slides.push({
            image: imageUrl,
            text: text
        });
    };
    for (var i = 0; i < config.carousel.length; i++) {
        $scope.addSlide(config.carousel[i].imageUrl, config.carousel[i].text);
    }
} ]).constant("CarouselPanelConfig", {
    config: {
        interval: 5e3,
        carousel: [ {
            imageUrl: "assets/front_yard_1.jpg",
            text: "Front Yard"
        }, {
            imageUrl: "assets/front_yard_2.jpg",
            text: "Side Yard"
        }, {
            imageUrl: "assets/interior_1.jpg",
            text: "Interior"
        }, {
            imageUrl: "assets/interior_2.jpg",
            text: "Interior"
        } ]
    }
});

angular.module("myApp.directive").directive("languageSelector", function() {
    return {
        restrict: "A",
        templateUrl: "build/html/language-selector.html",
        controller: "LanguageSelectorController",
        link: {
            post: function(scope, element, attribute) {}
        }
    };
}).controller("LanguageSelectorController", [ "$scope", "LanguageSelectorConfig", "TextProvider", function($scope, LanguageSelector, TextProvider) {
    $scope.languageSelector = LanguageSelector.config;
    TextProvider.interpretProperty("textId").in($scope.languageSelector, "LanguageSelector");
    $scope.defaultLanguage = {};
    $scope.defaultLanguage.default = "English";
    $scope.onLanguageSelected = function(selectedLanguage) {
        TextProvider.changeLanguage(selectedLanguage);
    };
    $scope.interpreter = TextProvider.getInterpreter();
} ]).constant("LanguageSelectorConfig", {
    config: [ {
        textId: "English",
        language: "English"
    }, {
        textId: "Vietnamese",
        language: "Vietnamese"
    } ]
});